/**
* @param {number} totalR
* @param {number} quantity
* @param {number} address
*/
function ReadRegistry(totalR, quantity, address) {
    if (totalR <= 0) {
        //node.status({ fill: "green", shape: "dot", text: "Done" });
        msg.payload = ""
        return msg;
    }

    setTimeout(function () {
        var remain = 0;

        if (totalR > quantity) {
            remain = totalR - quantity;

        } else if (totalR == quantity) {
            remain = quantity;
            remain = 0;
        } else if (totalR < quantity) {
            quantity = totalR;
            remain = 0;
        }

        if (quantity <= 0) {
            msg.payload = ""
            node.error("Quantity 0");
            return msg;
        } else {
            msg.payload = {
                'value': unit_id,
                'fc': msg.fc,
                'unitid': unit_id,
                'address': address,
                'quantity': quantity
            };
            node.send(msg);
            var newAddress = parseInt(address + quantity);
            ReadRegistry(remain, quantity, newAddress);
        }
    }, 1000);
}

