/**
* @param {Number} startAddress
* @param {Number} offset
* @param {Buffer} data
*/
function parseData(startAddress, offset, data){
    var startNdx= (offset - startAddress) ;
    var labelNdx = startNdx;
    var dataNdx = 0;
    var byteLength = data.length;

    if(startNdx>0) labelNdx=startNdx/2;

    if (functionType != "bitfield") {
        switch (swap) {
            case 16:
                data.swap16();
                break
            case 32:
                data.swap32();
                break;
            case 64:
                data.swap64();
                break;
        }
    }


    node.log("byteLength: " + byteLength)
    node.log("byteOffset: " + byteOffset)
    if (functionType != "bitfield" && functionType != "coil") {
        for (dataNdx; dataNdx < byteLength; dataNdx += byteOffset, labelNdx++){

            //node.log("dataNdx: " + dataNdx)
            var labelObj = values[labelNdx];
            var cValue = null;
            var strValue ="";

            if(labelObj.active==1){
                if(functionType == "floatbe"){
                    cValue = data.readFloatBE(dataNdx) ;
                    strValue = cValue.toFixed(2);
                } else if (functionType == "floatle") {
                    cValue = data.readFloatLE(dataNdx) ;
                    strValue = cValue.toFixed(2);
                } else if(functionType=="bigint64le"){
                    cValue = data.readBigInt64LE(dataNdx) ;
                    strValue = cValue.toString();
                }else if (functionType == "bigint64be") {
                    cValue = data.readBigInt64BE(dataNdx);
                    strValue = cValue.toString();
                }else if (functionType == "uint8") {
                    cValue = data.readUInt8(dataNdx);
                    strValue = cValue.toString();
                }else if (functionType == "uint16be") {
                    cValue = data.readUInt16BE(dataNdx);
                    strValue = cValue.toString();
                }else if (functionType == "uint16le") {
                    cValue = data.readUInt16LE(dataNdx);
                    strValue = cValue.toString();
                }


                var message = {};
                message.topic = topicStaticPart + "/" + labelObj.measure_type + "/" + sub_unit + "/" + labelObj.topic;

                var numValue=0;

                numValue = (Number(strValue) * labelObj.scale) + Number(labelObj.offset);
                //numValue = Number(numValue + Number(labelObj.offset))

                node.log("numValue: "+numValue)
                if(Number.isInteger(numValue)){

                    message.payload = {
                        [labelObj.label]: parseInt(numValue).toString() ,
                    };

                // meter[action_point] = {"measurement" : labelObj.label, "value": parseInt(numValue).toString(),"type": readingType, "topic_start" : vtopicstart, "topic_end" : vtopicend}
                }else{
                    message.payload = {
                        [labelObj.label]: parseFloat(numValue).toFixed(2),
                    };

                    //meter[action_point] = {"measurement" : labelObj.label, "value": parseFloat(numValue).toFixed(2),"type": readingType, "topic_start" : vtopicstart, "topic_end" : vtopicend}
                }

                //vstore.push(meter)
                node.send([message,[],[]]);
            }
        }
    } else {
        var bitString = ""
        var bitArray = []
        if (functionType == "coil") {
            bitString = data.readUint8(0).toString(2).padStart(8, "0")
            bitArray = bitString.split("")
        } else {
            bitString = data.readUInt16BE(0).toString(2).padStart(16, "0")
            bitArray = bitString.split("")
        }
        var bitLength = bitString.length - 1
        //node.log("Data string: " + bitString)
        //node.log("Data array: " + bitArray)

        var bitCount = Number(flow.get("bit2read"))
        dataNdx = 0;

        for (dataNdx; dataNdx < bitCount; dataNdx++) {
            var labelObj = values[dataNdx];
            if (labelObj.active == 1) {
                var bitMsg = {}
                bitMsg.topic = topicStaticPart + "/" + labelObj.measure_type + "/" + sub_unit + "/" + labelObj.topic;
                var bitIndex = bitLength - dataNdx
                bitMsg.payload = {
                    [labelObj.label]: Number(bitArray[bitIndex]),
                }

                node.send([bitMsg, [], []]);
            }
        }
    }
    //flow.set("$parent.vstore", vstore);
    return 1;
}

