function MetersLoop() {
    if(flow.get("stop")) {
        node.status({ fill: "blue", shape: "dot", text: "Stopped by user" })
        return;
    }
    return new Promise(function (resolve, reject) {
        (function WaitReadingGroup() {

            if (Boolean(flow.get("done"))) return resolve();

            setTimeout(SendOnTime, 1000);
            node.done();
        })();
    }).then(()=>{//Se la lista dei meters è terminata allora esco e lancio un nuovo gruppo di letture
        node.status({ fill: "green", shape: "dot", text: "Done" })
        var objMsg={};
        objMsg.payload = "Start New Group";
        node.send([[], objMsg], []);
        node.done();
    }).catch((e) =>{
        node.error("errore:"+e);
    });
}

function SendOnTime(){

    //if (flow.get("stop")) return;

    if (!Boolean(flow.get("pending"))){
        flow.set("pending", true);
        var element = flow.get("MeterArray")[meterNdx];
        meterNdx++;
        if(element.active==1){

            if(flow.get("retry")>0){
                node.status({ fill: "yellow", shape: "ring", text: "Resend: " + element.label });
                node.log("Resend: " + element.label);
            }else{
                node.status({ fill: "yellow", shape: "ring", text: "Send: " + element.label });    
                node.log("Send: " + element.label);
            }

            currentElement = element;
            flow.set("currentElement", currentElement);
            node.send([{
                "label": element.label,
                "id": element.id,
                "action_point": element.action_point,
                "sub_unit": element.sub_unit,
                "equip_id": element.equip_id,
                "Unit_Host_Name": element.Unit_Host_Name,
            }, [], []]);
        }else{
            node.status({ fill: "blue", shape: "ring", text: "Skipped by user: " + element.label });
            node.log("Skipped by user: " + element.label);
        }
        if (meterNdx == maxLength) { flow.set("done", true) }

    }else{
        if (fallBackCnt == 15) { 
            fallBackCnt=0;

            if(flow.get("retry")==2){
                flow.set("retry", 0);
                flow.set("pending", false);

                node.status({ fill: "red", shape: "ring", text: "Step forward..."});
                var message={};
                message.payload={"error" : "unit "+currentElement.label+" unresponsive - ID: "+currentElement.id};
                message.unit_id=currentElement.id;
                message.action_point=currentElement.action_point;
                message.sub_unit= currentElement.sub_unit;
                node.log("error: unit "+currentElement.label+" unresponsive - ID: "+currentElement.id);
                node.send([[], [], [message]]);
            }else{
                meterNdx--;
                node.status({ fill: "yellow", shape: "ring", text: "Retry "+currentElement.label});        
                flow.set("retry", (flow.get("retry")+1));
                flow.set("pending", false);
                node.log( "Retry "+currentElement.label);
            }
        }else{
            fallBackCnt++;
            if(flow.get("retry")>0){
                node.status({ fill: "yellow", shape: "ring", text: "Retry ("+(flow.get("retry")+1)+") "+currentElement.label });    
            }else{
                node.status({ fill: "yellow", shape: "ring", text: "Reading in progress "+currentElement.label });        
            }

        }

    }
    node.done();
    setTimeout(MetersLoop, 2000);
}

